                <ShieldCheck className="h-5 w-5 mr-2 text-green-500" />
                Your payment information is secure and encrypted
              </div>
            </div>

            <div className="mt-6 lg:hidden">
              <button
                type="submit"
                disabled={isSubmitting}
                className="w-full bg-blue-600 text-white py-3 px-4 rounded-md font-medium hover:bg-blue-700 transition-colors disabled:bg-blue-400 disabled:cursor-not-allowed"
              >
                {isSubmitting ? 'Processing...' : `Complete Order  $${total.toFixed(2)}`}
              </button>
            </div>
          </form>
        </div>

        {/* Order Summary */}
        <div className="lg:w-1/3">
          <div className="bg-white rounded-lg shadow-md p-6 sticky top-4">
            <h2 className="text-xl font-medium text-gray-900 mb-4">Order Summary</h2>

            <ul className="mb-4 divide-y divide-gray-200">
              {cartItems.map(item => (
                <li key={item.product.id} className="py-3 flex justify-between">
                  <div>
                    <p className="text-gray-900">{item.product.name}</p>
                    <p className="text-gray-500 text-sm">Qty: {item.quantity}</p>
                  </div>
                  <span className="text-gray-900 font-medium">
                    ${(item.product.price * item.quantity).toFixed(2)}
                  </span>
                </li>
              ))}
            </ul>

            <div className="space-y-3">
              <div className="flex justify-between">
                <p className="text-gray-600">Subtotal</p>
                <p className="text-gray-900 font-medium">${subtotal.toFixed(2)}</p>
              </div>

              <div className="flex justify-between">
                <p className="text-gray-600">Shipping</p>
                <p className="text-gray-900 font-medium">
                  {shipping === 0 ? 'Free' : `$${shipping.toFixed(2)}`}
                </p>
              </div>

              <div className="border-t border-gray-200 pt-3 flex justify-between">
                <p className="text-gray-900 font-medium">Total</p>
                <p className="text-xl text-gray-900 font-bold">
                  ${total.toFixed(2)}
                </p>
              </div>
            </div>

            <div className="mt-6 hidden lg:block">
              <button
                type="submit"
                form="checkout-form"
                disabled={isSubmitting}
                className="w-full bg-blue-600 text-white py-3 px-4 rounded-md font-medium hover:bg-blue-700 transition-colors disabled:bg-blue-400 disabled:cursor-not-allowed"
              >
                {isSubmitting ? 'Processing...' : 'Complete Order'}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CheckoutPage;

